def grid2d_ncsopf(input: wp.array2d(dtype=float), output: wp.array2d(dtype=float), 
           width: int, height: int):
    i, j = wp.tid()

    if i > 0 and i < height - 1 and j > 0 and j < width - 1:
        # Sobel edge detection
        gx = -input[i-1, j-1] - 2.0*input[i, j-1] - input[i+1, j-1] +               input[i-1, j+1] + 2.0*input[i, j+1] + input[i+1, j+1]
