def grid3d_kyuefg(input: wp.array3d(dtype=float), output: wp.array3d(dtype=float),
           nx: int, ny: int, nz: int):
    i, j, k = wp.tid()

    if i > 0 and i < nx - 1 and j > 0 and j < ny - 1 and k > 0 and k < nz - 1:
        center = input[i, j, k]
        neighbors = input[i-1, j, k] + input[i+1, j, k] +                    input[i, j-1, k] + input[i, j+1, k] +                    input[i, j, k-1] + input[i, j, k+1]
        output[i, j, k] = neighbors - 6.0 * center
    else:
        output[i, j, k] = 0.0

