@wp.kernel
def cond_135(input: wp.array(dtype=float), output: wp.array(dtype=float)):
    tid = wp.tid()
    x = input[tid]

    if x >= 0.06:
        output[tid] = x * 2.46
    else:
        output[tid] = x + 2.28

